import React, { useState, useEffect } from 'react';
import {
  Container,
  Typography,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Button,
  Box,
  Grid,
  Card,
  CardContent,
} from '@mui/material';
import axios from 'axios';

interface LeaderboardEntry {
  email: string;
  total_value: number;
}

const AdminDashboard = () => {
  const [leaderboard, setLeaderboard] = useState<LeaderboardEntry[]>([]);
  const [marketActive, setMarketActive] = useState(true);

  useEffect(() => {
    fetchLeaderboard();
    fetchMarketState();
  }, []);

  const fetchLeaderboard = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/leaderboard');
      setLeaderboard(response.data);
    } catch (error) {
      console.error('Error fetching leaderboard:', error);
    }
  };

  const fetchMarketState = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/market/state');
      setMarketActive(response.data.is_active);
    } catch (error) {
      console.error('Error fetching market state:', error);
    }
  };

  const handleMarketControl = async (action: 'start' | 'stop') => {
    try {
      await axios.post('http://localhost:5000/api/market/control', { action });
      setMarketActive(action === 'start');
    } catch (error) {
      console.error('Error controlling market:', error);
    }
  };

  return (
    <Container>
      <Typography variant="h4" gutterBottom>
        Admin Dashboard
      </Typography>

      <Grid container spacing={3}>
        <Grid item xs={12}>
          <Card>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Market Control
              </Typography>
              <Box sx={{ display: 'flex', gap: 2 }}>
                <Button
                  variant="contained"
                  color="primary"
                  onClick={() => handleMarketControl('start')}
                  disabled={marketActive}
                >
                  Start Market
                </Button>
                <Button
                  variant="contained"
                  color="secondary"
                  onClick={() => handleMarketControl('stop')}
                  disabled={!marketActive}
                >
                  Stop Market
                </Button>
              </Box>
            </CardContent>
          </Card>
        </Grid>

        <Grid item xs={12}>
          <Typography variant="h6" gutterBottom>
            Leaderboard
          </Typography>
          <TableContainer component={Paper}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Rank</TableCell>
                  <TableCell>User</TableCell>
                  <TableCell align="right">Total Value</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {leaderboard.map((entry, index) => (
                  <TableRow key={entry.email}>
                    <TableCell>{index + 1}</TableCell>
                    <TableCell>{entry.email}</TableCell>
                    <TableCell align="right">
                      ${entry.total_value.toFixed(2)}
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        </Grid>
      </Grid>
    </Container>
  );
};

export default AdminDashboard;
